// For handling the web requests for fetching data from the database
class API {

    static backend_url = "192.168.0.47:3333";

    // Perform a GET request to the backend for the current (most up-to-date temp/humidity)
    static async getCurrent() {
        try {
            // Perform GET request
            const response = await fetch(`${this.backend_url}/current`, {
                method: 'GET',
                headers: {}
            });

            // Parse response details and return
            const data = response.data;

            console.log(data);

            return { temp:data.temp, rh:data.rh, date:data.date, time:data.time }
        }
        catch (error) { console.error("Error fetching data: ", error); }
    }
}

export default API;
